	 public static int findPeakElement(ArrayList<Integer> arr) {
		
		int start = 0;
		int end = arr.size() - 1;
		int mid=0 ;
		while (start <= end) {
			mid = start + (end - start) / 2;
			if (mid != arr.size() - 1 && mid != 0) {
				if (arr.get(mid - 1) < arr.get(mid) && arr.get(mid) > arr.get(mid+1))
					return mid;

				else if (arr.get(mid) <= arr.get(mid+1))
					start = mid + 1;
				else 
					end = mid - 1;
			}
			
			else
				break;
		}
		return mid;
	}

